'use strict';

module.exports = class Util {
  static color(color) {
        if (typeof color === 'string') {
          if (color.toLowerCase() === 'random') return Math.floor(Math.random() * (0xffffff + 1));
          if (color.toLowerCase() === 'default') return 0;
          color = parseInt(color.replace('#', ''), 16);
        } else if (Array.isArray(color)) {
          color = (color[0] << 16) + (color[1] << 8) + color[2];
        }
        
        if (color < 0 || color > 0xffffff) throw new Error ("The color must be a hex code!");
        else if (color && isNaN(color)) throw new Error ("Could not convert color to number!");
        
        return color;
    }
}